        public string InitiateBatchTracking(BatchInput _newBatch)
        {
            var web3Srv = new Web3(BLOCK_CHAIN_URL);
            var unlockAccountResultSrv =
                web3Srv.Personal.UnlockAccount.SendRequestAsync(_newBatch.SenderAddress, BLOCK_CHAIN_PASSWD, new HexBigInteger(120));
            unlockAccountResultSrv.Wait();
            /////////////////////////////////////////////////
            //var newAcc = await web3Srv.Personal.NewAccount.SendRequestAsync("Password-123");
            //step-2: Got to the portal and fund
            //
            /////////////////////////////////////////////////
            //var balance = await web3Srv.Eth.GetBalance.SendRequestAsync(senderAddressSrv);
            //var byteCodeBatch =
            //    "";
            //var batchManagerAddress = "0x3487c5bef71f8182df1104ddbd3001388843cc52";//"0x3487c5bef71f8182df1104ddbd3001388843cc52";

            //balance = await web3Srv.Eth.GetBalance.SendRequestAsync(batchManagerAddress);
            //var accounts = await web3Srv.Eth.Accounts.SendRequestAsync();
            var contract = web3Srv.Eth.GetContract(BATCH_ABI, BATCH_CONTRACT_ADDRESS);
            var batchFunction = contract.GetFunction("initiateBatchTracking");

            //Error Event tracking
            Event _EvtError = contract.GetEvent("Error");
            var filterAllTask = _EvtError.CreateFilterAsync();
            filterAllTask.Wait();
            var filterAll = filterAllTask.Result;

            Event _EvtBatchTracking = contract.GetEvent("BatchTemperatureTrackingInitiated");
            var filterBatchTrackingTask = _EvtBatchTracking.CreateFilterAsync();
            filterBatchTrackingTask.Wait();
            var filterBatchTracking = filterBatchTrackingTask.Result;

            var resultBatchTask = batchFunction.SendTransactionAsync(_newBatch.SenderAddress,
                                                                        new HexBigInteger(500000),
                                                                        new HexBigInteger(0),
                                                                        _newBatch._batchId,
                                                                        _newBatch._deviceId,
                                                                        _newBatch._lstMinTemp,
                                                                        _newBatch._lstMaxTemp,
                                                                        _newBatch._lstExpireTickCount);
            resultBatchTask.Wait();
            var resultBatch = resultBatchTask.Result;
            var receiptTask = web3Srv.Eth.Transactions.GetTransactionReceipt.SendRequestAsync(resultBatch);
            receiptTask.Wait();
            var receipt = receiptTask.Result;
            int count = 0;
            while (receipt == null && count++ <= 15)
            {
                Thread.Sleep(5000);
                receiptTask = web3Srv.Eth.Transactions.GetTransactionReceipt.SendRequestAsync(resultBatch);
                receiptTask.Wait();
                receipt = receiptTask.Result;
            }

            //Error Event tracking
            var logTask = _EvtError.GetFilterChanges<ErrorEvent>(filterAll);
            logTask.Wait();
            var log = logTask.Result;
            if (log != null && log.Count > 0)
            {
                string logErrorInfo = Encoding.UTF8.GetString(log[0].Event.Info);
                return logErrorInfo;
            }                        
            //Batch Tracking Initiated Event
            var eventBatchTrakingTask = _EvtBatchTracking.GetFilterChanges<BatchTemperatureTrackingInitiatedEvent>(filterBatchTracking);
            eventBatchTrakingTask.Wait();
            var eventBatchTraking = eventBatchTrakingTask.Result;
            if (eventBatchTraking != null && eventBatchTraking.Count > 0)
            {
                string batchDesc = Encoding.UTF8.GetString(eventBatchTraking[0].Event.BatchId);
                if (!string.IsNullOrEmpty(batchDesc))
                {
                    return string.Empty;
                }
                else
                {
                    return "Initiate Batch tracking not processed";
                }
            }

            return "Initiate Batch tracking not processed";
        }

        public string AcknowledgeReceive(BatchInput batchInput)
        {
            var web3Srv = new Web3(BLOCK_CHAIN_URL);
            var unlockAccountResultSrv =
                web3Srv.Personal.UnlockAccount.SendRequestAsync(batchInput.SenderAddress, BLOCK_CHAIN_PASSWD, new HexBigInteger(120));
            unlockAccountResultSrv.Wait();

            var contract = web3Srv.Eth.GetContract(BATCH_ABI, BATCH_CONTRACT_ADDRESS);
            var batchFunction = contract.GetFunction("acknowledgeReceive");
            var _EvtBatchTemperatureTrackingInitiated = contract.GetEvent("OwnershipAcknowledged");
            //Error Event tracking
            Event _EvtError = contract.GetEvent("Error");
            var filterAllTask = _EvtError.CreateFilterAsync();
            filterAllTask.Wait();
            var filterAll = filterAllTask.Result;

            Event _EvtOwnershipAcknowledged = contract.GetEvent("OwnershipAcknowledged");
            var filterOwnershipAcknowledgedTask = _EvtOwnershipAcknowledged.CreateFilterAsync();
            filterOwnershipAcknowledgedTask.Wait();
            var filterOwnershipAcknowledged = filterOwnershipAcknowledgedTask.Result;

            var resultBatchTask = batchFunction.SendTransactionAsync(batchInput.SenderAddress,
                                                                        new HexBigInteger(500000),
                                                                        new HexBigInteger(0),
                                                                        batchInput._batchId
                                                                        );

            resultBatchTask.Wait();
            var resultBatch = resultBatchTask.Result;
            var receiptTask = web3Srv.Eth.Transactions.GetTransactionReceipt.SendRequestAsync(resultBatch);
            receiptTask.Wait();
            var receipt = receiptTask.Result;
            int count = 0;
            while (receipt == null && count++ <= 15)
            {
                Thread.Sleep(5000);
                receiptTask = web3Srv.Eth.Transactions.GetTransactionReceipt.SendRequestAsync(resultBatch);
                receiptTask.Wait();
                receipt = receiptTask.Result;
            }

            //Error Event tracking
            var logTask = _EvtError.GetFilterChanges<ErrorEvent>(filterAll);
            logTask.Wait();
            var log = logTask.Result;
            if (log != null && log.Count > 0)
            {
                string logErrorInfo = Encoding.UTF8.GetString(log[0].Event.Info);
                return logErrorInfo;
            }

            //Error Event tracking
            var eventOwnershipAcknowledgedTask = _EvtOwnershipAcknowledged.GetFilterChanges<OwnershipAcknowledgedEvent>(filterOwnershipAcknowledged);
            eventOwnershipAcknowledgedTask.Wait();
            var eventOwnershipAcknowledged = eventOwnershipAcknowledgedTask.Result;
            if (eventOwnershipAcknowledged != null && eventOwnershipAcknowledged.Count > 0)
            {
                string batchDesc = Encoding.UTF8.GetString(eventOwnershipAcknowledged[0].Event.BatchId);
                if (!string.IsNullOrEmpty(batchDesc))
                {
                    return string.Empty;
                }
                else
                {
                    return "Initiate Batch tracking not processed";
                }
            }

            return "Initiate Batch tracking not processed";
        }

        public BatchDao GetBatchInfoSync(string batchCode)
        {
            try
            {
                var web3 = new Web3(BLOCK_CHAIN_URL);
                contract = web3.Eth.GetContract(BATCH_ABI, BATCH_CONTRACT_ADDRESS);

                ITypeEncoder byte32Encoder = new Bytes32TypeEncoder();
                var batchDetails = contract.GetFunction("getBatchInfo");
                var batchTask = batchDetails.CallDeserializingToObjectAsync<BatchDao>(byte32Encoder.Encode(batchCode));
                batchTask.Wait();
                return batchTask.Result;
            }
            catch (Exception ex)
            {
                throw ex;
            }
        }

        
        public bool IsValidBatch(string batchCode)
        {
            try
            {
                var web3 = new Web3(BLOCK_CHAIN_URL);
                contract = web3.Eth.GetContract(BATCH_ABI, BATCH_CONTRACT_ADDRESS);

                ITypeEncoder byte32Encoder = new Bytes32TypeEncoder();
                var batchDetails = contract.GetFunction("isValidBatch");
                var batchTask = batchDetails.CallAsync<bool>(byte32Encoder.Encode(batchCode));
                batchTask.Wait();
                return batchTask.Result;
            }
            catch (Exception ex)
            {
                throw ex;
            }
        }

        public async Task<BatchDao> GetBatchInfo(string batchId)
        {
            try
            {
                var web31 = new Web3(BLOCK_CHAIN_URL);
                var contract1 = web31.Eth.GetContract(BATCH_ABI, BATCH_CONTRACT_ADDRESS);

                ITypeEncoder byte32Encoder = new Bytes32TypeEncoder();
                var byteBatchId = byte32Encoder.Encode(batchId);
                var batchDetails = contract1.GetFunction("getBatchInfo");
                var batch = await batchDetails.CallDeserializingToObjectAsync<BatchDao>(byteBatchId).ConfigureAwait(false);
                return batch;
            }
            catch (Exception ex)
            {
                throw ex;
            }            
        }
        
        public async Task<long> GetAssociatedDeviceId(string batchId)
